import React from "react"
import AddUser from "./addUser"
import { IoCloseCircleSharp, IoHammerSharp } from 'react-icons/io5' //обязательно указывать эти функции в импорте

class User extends React.Component {

  constructor (props){
    super(props)
    this.state = {
      editform: false
    }
  }

    user = this.props.user //просто сократить код
    render(){
      return(
            <div className="user" >
                <IoCloseCircleSharp onClick={()=> this.props.onDelete(this.user.id)} className="delete-icon"/>
                <IoHammerSharp onClick={() => {
                  this.setState({
                    editform: !this.state.editform
                  })
                }} className="edit-icon"/>
                <h3>{this.user.firstname} {this.user.lastname}</h3>
                <p>{this.user.bio}</p>
                <b>{this.user.isHappy ? 'Счастлив :)' : 'Не особо :('}</b>

                {this.state.editform && <AddUser onAdd={this.props.onEdit}/>}
            </div>
      )
    } 
  }

export default User
